import java.io.*;
import java.util.*;
public class Main1 {
    public static void main(String args[]){
        Scanner scn = new Scanner(System.in);
        int n = scn.nextInt();
        //printIncreasing(n);
        printDecreasing(n); }
    public static void printIncreasing(int n){
        if(n==0){ //base case
            return; }
        printIncreasing(n-1); //faith
        System.out.println(n); //function do it by itselt
    }
    public static void printDecreasing(int n){
        if(n==0)
        {
            return;
        }
        System.out.println(n);
        printDecreasing(n-1);
    }}    

