import util.java.*;

public class Pattern5 {      
    public static void main(String[] args) {         
        Scanner scn = new Scanner(System.in);         
        int n = scn.nextInt();         
        int nspaces = n/2, nstars=1;              
        for(int i =1;i<=n;i++){             
            for(int j =1;j<=nspaces;j++){                 
                System.out.print("\t");             
            }             
            for(int j=1; j<=nstars; j++){             
                System.out.println("*\t");             
            }             //new line             
            System.out.println();              
            if(i<=n/2){                  
                nspaces--;                 
                nstars = nstars +2;             
            }else{                 
                nspaces++;                 
                nstars = nstars -2;             
            }                      
        }     } }