import java.util.*;

public class multiplySystem{

public static void main(String[] args) {
    Scanner scn = new Scanner(System.in);
    int b = scn.nextInt();
    int n1 = scn.nextInt();
    int n2 = scn.nextInt();

    int d = getProduct(b, n1, n2);
    System.out.println(d);
 }
public static int getSum(int b, int n1, int n2){
     int ans =0;
     int mul =1;
     int carry =0;

     while ( n1>0 || n2>0 || carry >0 ) {
         int d1 = n1 % 10;
         int d2 = n2 % 10;
         int sum = carry + d1 + d2;

         carry = sum/b; // carry update
         ans = ans + (sum%b)*mul;
         mul = mul * 10;
         n1 = n1/10;
         n2 = n2/10;

     }
     return ans;
 }
 public static int singleDigitPro(int b , int n1 , int d2){
   int ans = 0;
   int carry = 0;
   int mul = 1;
   while(n1>0 || carry>0){
     int d1 = n1%10;
     n1 /= 10;
     int m = d1*d2 + carry;
     ans = ans + (m%b)*mul;
     carry = m/b;
    mul = mul * 10;
   }
   return ans;
 }


public static int getProduct(int b, int n1, int n2){
     // write your code here
   int fres = 0;
    int mul = 1;
   while(n2>0){
    int d2 = n2%10;
    n2 = n2/10;
    int sp = singleDigitPro(b , n1 ,d2 ) * mul;
    fres = getSum(b , fres , sp);
    mul *=10;

   }

return fres;

 }
}